INSERT INTO `solutions` (`QuestionID`, `SolutionText`, `SolutionPicture`, `Hint`, `AnswerText`, `AnswerPic`) VALUES ('40240801', '0.85 + x = 1\r\nx = 1 - 0.85 = 0.15', NULL, 'The total possible probability is 1.', '0.15', NULL), ('40240802', '2/5 * 2/5 = 4/25', NULL, 'Consecutive probabilities may require multiplication.', '4/25', NULL), ('40240803', '9/35 in decimal is approximately 0.257, which is less than 0.26, thus Sammy wins', NULL, 'Convert both probabilities to decimal', 'Sammy', NULL), ('40240804', 'The number of rooms ending in zero is 30, thus probability is 30/300.', NULL, 'Count the number of room ending in zero', '30/300', NULL), ('40240805', '3/9 * 2/8 = 6/72', NULL, 'Without replacement means that during the second pick, there\'ll be less number of pencils available.', '6/72', NULL), ('40240806', '(1-0.2)*(1-0.2)*(1-0.2)=0.512', NULL, 'Find the probability that Xsara misses in one of the throws.', '', NULL), ('40240807', '0.4 + x = 1\r\nx = 1 - 0.4 = 0.6', NULL, 'The total possible probability is 1.', '0.6', NULL), ('40240808', '1/3 + x = 1\r\nx = 1 - 1/3 = 2/3', NULL, 'The total possible probability is 1.', '2/3', NULL), ('40240809', '8/19', NULL, '1 is not a prime number', '8/19', NULL), ('40240810', '(68 + 81 + 74 + 89 + x)/5 = 80\r\n312 + x = 400\r\nx = 88', NULL, 'Recall the formula for calculating mean', '88', NULL);